std::vector coverBlacklist = {37, 39};

void convertGrid(int (&gridArray)[GRID_SIZE][GRID_SIZE]) {
	for (int i = 0; i < GRID_SIZE; i++) {
		// flip 1/2s and zeroes
		// https://stackoverflow.com/questions/3450860/check-if-a-stdvector-contains-a-certain-object#3450906
		// cover everything left of the frame
		bool coverTillNext = !(std::find(coverBlacklist.begin(), coverBlacklist.end(), i) != coverBlacklist.end());
		for (int j = 0; j < GRID_SIZE; j++) {
			
			if (gridArray[i][j] != 0) {
				coverTillNext = (gridArray[i][j] == 3);
				gridArray[i][j] = 0;
				continue;
			}

			if (coverTillNext) continue;

			int fillWithOne = (i + j % 2) % 2;
			gridArray[i][j] = fillWithOne ? 1 : 2;
		}
	}

	// cover everything right of frame
	for (int i = 0; i < GRID_SIZE; i++) {
		for (int j = GRID_SIZE - 1; j >= 0; j--) {
			if (gridArray[i][j] != 0) gridArray[i][j] = 0;
			else break;
		}
	}
}
